import { Button, Typography, Paper, TextField } from "@material-ui/core";
import { useState } from "react";

const AppointmentForm = () => {
  const [step, setStep] = useState(0);

  switch (step) {
    case 0:
      return <AssistanceType setStep={setStep} />;
    case 1:
      return <FirstStep setStep={setStep} />;
    case 2:
      return <SecondStep setStep={setStep} />;
    case 3:
      return <CompletedStep />;
  }
};

const AssistanceType = ({ setStep }) => {
  return (
    <Paper
      align="center"
      style={{ height: "40vh", marginTop: 20, padding: 20 }}
    >
      <Typography variant="h5">What do you seek?</Typography>
      <Button variant="contained" onClick={() => setStep(1)}>
        Counseling
      </Button>
      <br />
      <Button variant="contained" color="primary" onClick={() => setStep(1)}>
        Legal Guidance
      </Button>
      <br />
      <Button variant="contained" color="secondary" onClick={() => setStep(1)}>
        Legal Action
      </Button>
    </Paper>
  );
};

const FirstStep = ({ setStep }) => {
  return (
    <Paper
      align="center"
      style={{ height: "40vh", marginTop: 20, padding: 20 }}
    >
      <h1>step 1</h1>

      <FormControl>
        <InputLabel>Category</InputLabel>
        <Select value={age} onChange={handleChange}>
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
        <FormHelperText>Some important helper text</FormHelperText>
      </FormControl>

      <TextField
        name="description"
        placeholder="Briefly Describe your problem"
        fullWidth
        multiline
        variant="outlined"
      />

      <Button onClick={() => setStep(0)}>Back</Button>
      <Button onClick={() => setStep(2)}>Next</Button>
    </Paper>
  );
};

const SecondStep = ({ setStep }) => {
  return (
    <Paper
      align="center"
      style={{ height: "40vh", marginTop: 20, padding: 20 }}
    >
      <h1>step 2</h1>
      <Button onClick={() => setStep(1)}>Back</Button>
      <Button onClick={() => setStep(3)}>Submit</Button>
    </Paper>
  );
};

const CompletedStep = () => {
  return (
    <Paper
      align="center"
      style={{ height: "40vh", marginTop: 20, padding: 20 }}
    >
      <h1>Completed!</h1>
    </Paper>
  );
};

export default AppointmentForm;
