import AssistanceType from "./assistanceType";
import FirstStep from "./firstStep";
import SecondStep from "./secondStep";

import { Paper } from "@material-ui/core";
import { Component } from "react";
import { render } from "react-dom";

class AppointmentForm extends Component {
  state = {
    step: 0,
  };

  handleStepChange = (step) => {
    this.setState({ step: step });
  };

  render() {
    const { step } = this.state;

    let CurrentStep;

    switch (step) {
      case 0:
        CurrentStep = <AssistanceType setStep={this.handleStepChange} />;
        break;
      case 1:
        CurrentStep = <FirstStep setStep={this.handleStepChange} />;
        break;
      case 2:
        CurrentStep = <SecondStep setStep={this.handleStepChange} />;
        break;
      case 3:
        CurrentStep = <CompletedStep />;
    }

    return (
      <Paper
        align="left"
        style={{
          position: "relative",
          height: 300,
          marginTop: 20,
          padding: 20,
        }}
      >
        {CurrentStep}
      </Paper>
    );
  }
}

const CompletedStep = () => {
  return (
    <div align="center">
      <h1>Completed!</h1>
    </div>
  );
};

export default AppointmentForm;
