import { Component } from "react";

import AssistanceType from "./assistanceType";
import FirstStep from "./firstStep";
import SecondStep from "./secondStep";

import { Typography } from "@material-ui/core";

import { withCookies, Cookies } from "react-cookie";

class AppointmentForm extends Component {
  static state = {
    step: 0,
    assistanceType: "",
    category: "",
    description: "",
    meetingType: "",
    meetingTime: "",
  };

  handleStepChange = (step) => {
    this.setState({ step: step });
  };

  handleFormDataChange = (field, value) => {
    this.setState({ [field]: value });
  };

  render() {
    const {
      step,
      assistanceType,
      category,
      description,
      meetingType,
      meetingTime,
    } = this.state;

    const values = {
      assistanceType,
      category,
      description,
      meetingType,
      meetingTime,
    };

    switch (step) {
      case 0:
        return (
          <AssistanceType
            setStep={this.handleStepChange}
            setFormData={this.handleFormDataChange}
          />
        );

      case 1:
        return (
          <FirstStep
            category={category}
            description={description}
            setStep={this.handleStepChange}
            setFormData={this.handleFormDataChange}
          />
        );

      case 2:
        return (
          <SecondStep
            type={meetingType}
            time={meetingTime}
            setStep={this.handleStepChange}
            setFormData={this.handleFormDataChange}
          />
        );

      case 3:
        return <CompletedStep formData={values} />;
    }
  }
}

const CompletedStep = ({ formData }) => {
  return (
    <div>
      <h1 align="center">Completed!</h1>
      <Typography variant="body1">{JSON.stringify(formData)}</Typography>
    </div>
  );
};

export default AppointmentForm;
