import {
  Button,
  Typography,
  TextField,
  FormControl,
  Select,
  MenuItem,
  Grid,
} from "@material-ui/core";
import { useForm } from "react-hook-form";

const categories = [
  "Sexual Harrassment",
  "Gender Discrimination",
  "Domestic Violence",
  "Ragging/Bullying",
  "Eve Teasing",
  "Social Media Harrassment",
  "Dowry",
  "Snatching/Theft",
];

const FirstStep = ({ category, setStep, setFormData }) => {
  const { register, handleSubmit } = useForm();

  const handleDescriptionChange = (data) => {
    setFormData("description", data);
  };

  const handleCategoryChange = (event) => {
    setFormData("category", event.target.value);
  };

  return (
    <React.Fragment>
      <h1>Step 1</h1>

      <Grid container alignItems="center" style={{ marginBottom: 10 }}>
        <Grid item>
          <Typography variant="body1">
            Please select a category of your problem:
          </Typography>
        </Grid>

        <Grid item>
          <FormControl style={{ minWidth: 200, marginLeft: 10 }}>
            <Select value={category} onChange={handleCategoryChange}>
              <MenuItem value="">
                <em>None</em>
              </MenuItem>

              {categories.map((ctg, index) => (
                <MenuItem key={index} value={ctg}>
                  {ctg}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
      </Grid>

      <Typography variant="body1">Please describe your problem:</Typography>

      <form onSubmit={handleSubmit(handleCategoryChange)}>
        <TextField
          inputRef={register}
          name="description"
          placeholder="Write a few lines about your problem..."
          fullWidth
          multiline
          variant="outlined"
        />

        <div style={{ position: "absolute", bottom: 20, right: 20 }}>
          <Button type="submit" onClick={() => setStep(2)}>
            Next
          </Button>
        </div>
      </form>
    </React.Fragment>
  );
};

export default FirstStep;
