import {
  Button,
  Typography,
  Paper,
  TextField,
  FormControl,
  Select,
  MenuItem,
  Grid,
  RadioGroup,
  Radio,
  FormControlLabel,
} from "@material-ui/core";
import { useState } from "react";

const categories = [
  "Sexual Harrassment",
  "Gender Discrimination",
  "Domestic Violence",
  "Ragging/Bullying",
  "Eve Teasing",
  "Social Media Harrassment",
  "Dowry",
  "Snatching/Theft",
];

const AppointmentForm = () => {
  const [step, setStep] = useState(0);

  switch (step) {
    case 0:
      return <AssistanceType setStep={setStep} />;
    case 1:
      return <FirstStep setStep={setStep} />;
    case 2:
      return <SecondStep setStep={setStep} />;
    case 3:
      return <CompletedStep />;
  }
};

const FirstStep = ({ setStep }) => {
  const [category, setCategory] = React.useState("");

  const handleCategoryChange = (event) => {
    setCategory(event.target.value);
  };

  return (
    <Paper
      align="left"
      style={{
        position: "relative",
        height: 300,
        marginTop: 20,
        padding: 20,
      }}
    >
      <h1>Step 1</h1>

      <Grid container alignItems="center" style={{ marginBottom: 10 }}>
        <Grid item>
          <Typography variant="body1">
            Please select a category of your problem:
          </Typography>
        </Grid>

        <Grid item>
          <FormControl style={{ minWidth: 200, marginLeft: 10 }}>
            <Select value={category} onChange={handleCategoryChange}>
              <MenuItem value="">
                <em>None</em>
              </MenuItem>

              {categories.map((ctg, index) => (
                <MenuItem value={index}>{ctg}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
      </Grid>

      <Typography variant="body1">Please describe your problem:</Typography>

      <TextField
        name="description"
        placeholder="Write a few lines about your problem..."
        fullWidth
        multiline
        variant="outlined"
      />

      <div style={{ position: "absolute", bottom: 20, right: 20 }}>
        <Button onClick={() => setStep(0)}>Back</Button>
        <Button onClick={() => setStep(2)}>Next</Button>
      </div>
    </Paper>
  );
};

const SecondStep = ({ setStep }) => {
  const [meetingType, setMeetingType] = React.useState("");

  const handleMeetingTypeChange = (event) => {
    setMeetingType(event.target.value);
  };

  const [meetingTime, setMeetingTime] = React.useState("");

  const handleMeetingTimeChange = (event) => {
    setMeetingTime(event.target.value);
  };

  return (
    <Paper
      align="left"
      style={{
        position: "relative",
        height: 300,
        marginTop: 20,
        padding: 20,
      }}
    >
      <h1>Step 2</h1>

      <Grid container alignItems="center">
        <Grid item>
          <Typography variant="body1">Please select a meeting type:</Typography>
        </Grid>

        <Grid item>
          <FormControl style={{ marginLeft: 10 }}>
            <RadioGroup value={meetingType} onChange={handleMeetingTypeChange}>
              <Grid container>
                <Grid item>
                  <FormControlLabel
                    value="text"
                    control={<Radio />}
                    label="Text Messaging"
                  />
                </Grid>
                <Grid item>
                  <FormControlLabel
                    value="voice"
                    control={<Radio />}
                    label="Voice Call"
                  />
                </Grid>
                <Grid item>
                  <FormControlLabel
                    value="video"
                    control={<Radio />}
                    label="Video Call"
                  />
                </Grid>
              </Grid>
            </RadioGroup>
          </FormControl>
        </Grid>
      </Grid>

      <Grid container alignItems="center">
        <Grid item>
          <Typography variant="body1">
            Please select your convenient time for the meeting:
          </Typography>
        </Grid>

        <Grid item>
          <FormControl style={{ marginLeft: 10 }}>
            <RadioGroup value={meetingTime} onChange={handleMeetingTimeChange}>
              <Grid container>
                <Grid item>
                  <FormControlLabel
                    value="morning"
                    control={<Radio />}
                    label="10AM-12PM"
                  />
                </Grid>
                <Grid item>
                  <FormControlLabel
                    value="afterNoon"
                    control={<Radio />}
                    label="2PM-4PM"
                  />
                </Grid>
              </Grid>
            </RadioGroup>
          </FormControl>
        </Grid>
      </Grid>

      <div style={{ position: "absolute", bottom: 20, right: 20 }}>
        <Button onClick={() => setStep(1)}>Back</Button>
        <Button onClick={() => setStep(3)}>Next</Button>
      </div>
    </Paper>
  );
};

const CompletedStep = () => {
  return (
    <Paper align="center" style={{ height: 300, marginTop: 20, padding: 20 }}>
      <h1>Completed!</h1>
    </Paper>
  );
};

export default AppointmentForm;
