import AssistanceType from "./assistanceType";
import FirstStep from "./firstStep";
import SecondStep from "./secondStep";

import { Paper, TabPanel, Tabs, Tab } from "@material-ui/core";
import { Component } from "react";

class AppointmentForm extends Component {
  state = {
    step: 0,
    assistanceType: "",
    category: "",
    description: "",
    meetingType: "",
    meetingTime: "",
  };

  handleStepChange = (step) => {
    this.setState({ step: step });
  };

  handleFormDataChange = (field, value) => {
    this.setState({ [field]: value });
  };

  render() {
    console.log(this.state);

    const { step } = this.state;

    let CurrentStep;

    switch (step) {
      case 0:
        CurrentStep = (
          <AssistanceType
            setStep={this.handleStepChange}
            setFormData={this.handleFormDataChange}
          />
        );
        break;
      case 1:
        CurrentStep = (
          <FirstStep
            category={this.state.category}
            description={this.state.description}
            setStep={this.handleStepChange}
            setFormData={this.handleFormDataChange}
          />
        );
        break;
      case 2:
        CurrentStep = (
          <SecondStep
            type={this.state.meetingType}
            time={this.state.meetingTime}
            setStep={this.handleStepChange}
            setFormData={this.handleFormDataChange}
          />
        );
        break;
      case 3:
        CurrentStep = <CompletedStep />;
    }

    const value = 0;

    return (
      <Paper
        align="left"
        style={{
          position: "relative",
          height: 300,
          marginTop: 20,
          padding: 20,
        }}
      >
        {CurrentStep}

        <Tabs
          variant="scrollable"
          classes={{
            root: classes.tabs,
          }}
          value={this.state.option}
          indicatorColor="secondary"
          textColor="secondary"
          onChange={this.handleOptionSelect}
        >
          {options.map((option) => (
            <Tab
              key={option}
              classes={{
                root: classes.tab,
              }}
              label={option}
            />
          ))}
        </Tabs>

        {/*
        <Tabs
          orientation="vertical"
          variant="scrollable"
          value={value}
          //onChange={handleChange}
          //className={classes.tabs}
        >
          <Tab value={value} index={0}>
            Item One
          </Tab>
          <Tab value={value} index={1}>
            Item Two
          </Tab>
          <Tab value={value} index={2}>
            Item Three
          </Tab>
        
        </Tabs>*/}
      </Paper>
    );
  }
}

const CompletedStep = () => {
  return (
    <div align="center">
      <h1>Completed!</h1>
    </div>
  );
};

export default AppointmentForm;
